.footer{
    margin-top: 270px;
    width:100vw;
    // display: flex;
    // justify-content: space-between;
    .attribution{
        position: absolute;
        left:50%;
        transform: translate(-50%);
        bottom:16px;
        
    }
    button{
        position: absolute;
        bottom:16px;
        padding: 7px 25px;
        right: 16px;
        border-radius: .4rem;
        border:3px solid $header;
        color: lighten( $header, 20%);
        background: none;
        font-weight: 600;
        letter-spacing: 2px;
        &:hover{
            background-color: rgba($color: white, $alpha: .8);
            color: $dark-text;
            cursor: pointer;
        }



    }
}

sources.scss\\_modal.scss:
.modal-container{
    display:flex;
    position: absolute;
    z-index:10;
    height: 100vh;
    width: 100vh;
    align-items: center;
    justify-content: center;

    .modal-box{
        background-color: white;
        border-radius: 7px;
        padding: 30px;
        color: $dark-text;
        img{
            width: 100%;
       
        }
        .modal__header{
            display: flex;
            justify-content: space-between;
            margin-bottom: 30px;
            h1{
                text-transform: uppercase;
                margin: 0;
                padding: 0;
            }
        }
    }
    button{
        background: none;
        border:none;
        align-self: center;
        color:$dark-text;
        &:hover{
            cursor:pointer;
        }

    }
}
